import React from 'react';

const PHOTO_FALLBACK = 'data:image/svg+xml;base64,';

const Avatar = ({ member, style = {} }) => (
  <a
    href='#'
    className='avatar avatar--person'
    style={{
      ...style,
      'backgroundImage': `url(${(member.photo || {}).thumb_link || PHOTO_FALLBACK})`
    }}>{member.name}</a>
);

export default Avatar;
